get_round_score <- function(opponent, player) {

  if (opponent == player) {
    bonus = +3
  } else if ((player > opponent | (player == 1 & opponent == 3)) & !(player == 3 & opponent == 1)) {
    bonus = +6
  } else {
    bonus = 0
  }

  return(player + bonus)

}


