  if (outcome == 2) {
    bonus = 3
    player = opponent
  } else if(outcome == 1){
    bonus = 0
    if (opponent == 1) {
      player = 3
    } else {
      player = opponent - 1
    }
  } else if(outcome == 3){
    bonus = 6
    if (opponent == 3) {
      player = 1
    } else {
      player = opponent + 1
    }
  }

  return(player + bonus)

}


