is_visible <- function(x){
  results <- vector(mode = "logical", length = length(x))

  for (i in seq_along(x)) {
    if(i == 1 || i == length(x)){
      results[i] <- TRUE
    } else if (all(x[i] > x[1:i-1]) || all(x[i] > x[(i+1):length(x)])) {
      results[i] <- TRUE
    } else {
      results[i] <- FALSE
    }
  }
  results

}

